games.addEventListener('change', function (e) {
  var target = e.target;
  var value = target.options[target.selectedIndex].value;
  play.disabled = false;

  program.loadFromHref('./roms/' + value, function (e) {
    if (e) {
      return source.innerHTML = 'ERROR!';
    }
    emulator.load(program.bytes);
    play.disabled = false;
    source.innerHTML = 'INST  MEMLOC  OPCODE\n----  ------  ------\n' + program.toString();
  });
});

var emulator = new chip8.Emulator();
emulator.beep = beep;

document.addEventListener('keydown', function (e) {
  emulator.keydown(e.keyCode - 0x60);
});

document.addEventListener('keyup', function (e) {
  emulator.keyup(e.keyCode - 0x60);
});

play.addEventListener('click', function () {
  message.innerHTML = '&nbsp;';
  emulator.reset();
  emulator.run();
  window.setTimeout(updateScreen, 0);
});

var texts = new Array(0x20);
for (var i = 0x00; i < 0x20; i += 1) {
  texts[i] = new Array(0x40);
}
var rowText = new Array(0x20);
function updateScreen() {
  if (emulator.dirty) {
    var gfx = emulator.gfx;
    for (var y = 0; y < 32; y += 1) {
      for (var x = 0; x < 64; x += 1) {
        texts[y][x] = gfx[y * 64 + x] > 0? '*' : ' ';
      }
      rowText[y] = texts[y].join('');
    }

    screen.innerHTML = rowText.join('\n');
  }
  if (emulator.running) {
    window.setTimeout(updateScreen, 0);
  } else {
    message.innerHTML = 'GAME OVER!';
  }
}

window.focus();

var snd = new Audio("data:audio/wav;base64,");

function beep() {
  setTimeout(function () {
    snd.play();
  }, 0);
}
